const int up = 22;
const int down = 17;
const int leftn = 6;
const int rightn = 27;

lv_indev_state_t state;


uint32_t lastButton;

void up_press() {
    printf("Up");
    state = LV_INDEV_STATE_PRESSED;
    lastButton = LV_KEY_UP;

}

void up_rel() {
    state = LV_INDEV_STATE_RELEASED;
    lastButton = LV_KEY_UP;

}

void down_press() {
    state = LV_INDEV_STATE_PRESSED;
    lastButton = LV_KEY_DOWN;

}

void down_rel() {
    state = LV_INDEV_STATE_RELEASED;
    lastButton = LV_KEY_DOWN;

}

void left_press() {
    state = LV_INDEV_STATE_PRESSED;
    lastButton = LV_KEY_LEFT;

}

void left_rel() {
    state = LV_INDEV_STATE_RELEASED;
    lastButton = LV_KEY_LEFT;

}



void right_rel() {
    state = LV_INDEV_STATE_RELEASED;
    lastButton = LV_KEY_RIGHT;

}

void right_press() {
    state = LV_INDEV_STATE_PRESSED;
    lastButton = LV_KEY_RIGHT;

}

void pad_init() {
    pinMode(up, INPUT);
    pinMode(down, INPUT);
    pinMode(leftn, INPUT);
    pinMode(rightn, INPUT);
    pullUpDnControl(up, PUD_UP);
    pullUpDnControl(down, PUD_UP);
    pullUpDnControl(leftn, PUD_UP);
    pullUpDnControl(rightn, PUD_UP);

    wiringPiISR(up, INT_EDGE_RISING, &up_rel);
    wiringPiISR(up, INT_EDGE_FALLING, &up_press);

    wiringPiISR(down, INT_EDGE_FALLING, &down_press);
    wiringPiISR(down, INT_EDGE_RISING, &up_rel);

    wiringPiISR(leftn, INT_EDGE_FALLING, &left_press);
    wiringPiISR(leftn, INT_EDGE_RISING, &left_rel);

    wiringPiISR(rightn, INT_EDGE_FALLING, &right_press);
    wiringPiISR(rightn, INT_EDGE_RISING, &right_rel);

}



void pad_read(lv_indev_drv_t* drv, lv_indev_data_t* data) {
    data->key = lastButton;
    printf("RAN");
    data->state = state;
}



